package com.example.formula1

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import android.util.Log
import android.widget.ImageView
import com.bumptech.glide.Glide

class GridItemAdapter(private var dataSet: Array<ConStanding>): RecyclerView.Adapter<GridItemAdapter.ViewHolder>() {

    class ViewHolder(itemView: View): RecyclerView.ViewHolder(itemView){
        val consName: TextView = itemView.findViewById(R.id.textView23)
        val pos: TextView  = itemView.findViewById(R.id.textView27)
        val numPoints: TextView  = itemView.findViewById(R.id.textView25)
        val image: ImageView = itemView.findViewById(R.id.imageView4)

    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.grid_item, parent, false)
        return ViewHolder(view)
    }

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {
        holder.pos.text=dataSet.get(position).position
        holder.numPoints.text=dataSet.get(position).points
        holder.consName.text=dataSet.get(position).constructorData.name

        var wikiUrl = "data:image/png;base64,"

        when (dataSet.get(position).constructorData.name){
            "Red Bull" -> wikiUrl="data:image/jpeg;base64,"
            "Ferrari" -> wikiUrl="data:image/png;base64,"
            "Mercedes" -> wikiUrl="https://az1.hatstoremedia.com/hatstore/images/image-mercedes-amg-petronas-motorsport-2018-08-09-121125657/555/555/0/mercedes-amg-petronas-motorsport.png"
            "Alpine F1 Team" -> wikiUrl="https://upload.wikimedia.org/wikipedia/commons/thumb/7/7e/Alpine_F1_Team_Logo.svg/1200px-Alpine_F1_Team_Logo.svg.png"
            "McLaren" -> wikiUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSEi4V6kZAM8iz_kJbkoV5HCIKKgvHxOfPjhjAv3DsYnUJtsV8QNutyUJimieZyNFBlnkg&usqp=CAU"
            "Alfa Romeo" -> wikiUrl="data:image/png;base64,"
            "Haas F1 Team" -> wikiUrl="https://logosandtypes.com/wp-content/uploads/2020/07/haas-F1-team.png"
            "AlphaTauri" -> wikiUrl="https://upload.wikimedia.org/wikipedia/en/thumb/0/09/Scuderia_Alpha-Tauri.svg/1200px-Scuderia_Alpha-Tauri.svg.png"
            "Aston Martin" -> wikiUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSJNZ3lk6fiqZMVbvn4REE36kMMJcbHRjLxqp1a8qzPRgRacJr2SXO1JxgbjN5v9FfWLoU&usqp=CAU"
            "Williams" -> wikiUrl="https://www.gp1.hr/wp-content/uploads/2021/03/williams-racing-logo.jpg"



        }

        Glide.with(holder.itemView.context).load(wikiUrl).fitCenter().into(holder.image)





    }
    override fun getItemViewType(position: Int): Int {
        return R.layout.grid_item
    }

    fun setItemList(newDataSet: Array<ConStanding>) {
        this.dataSet=newDataSet
        notifyDataSetChanged()
        Log.d("asko","resetovaoSam")
    }

    override fun getItemCount(): Int {
        return dataSet.size
    }
}